"use strict";
const center =  (month, year) => {

  let top = month + ' ' + year;
  let topPart = top.length;
  let frontSpace = (20 - topPart)/2 -1;
  let space = ' ';
  for(var i = 0; i<=frontSpace; i++){

      top = space.concat(top);

  };

  const show = top + '\n' + 'Su Mo Tu We Th Fr Sa\n';

  return show;
};

module.exports = center;
